import { Component, OnDestroy, OnInit } from '@angular/core';
import { Observable, Subscription } from 'rxjs';
import { LayoutService } from '../../../core/layout.service';
import { PageInfoService, PageLink } from '../../../core/page-info.service';

@Component({
  selector: 'app-page-title',
  templateUrl: './page-title.component.html',
})
export class PageTitleComponent implements OnInit, OnDestroy {
  private unsubscribe: Subscription[] = [];
  showTitle: boolean = true;
  showBC: boolean = true;
  title$: Observable<string>;
  description$: Observable<string>;
  bc$: Observable<Array<PageLink>>;
  pageTitleCssClass: string = '';
  pageTitleDirection: string = 'row';

  constructor(
    private pageInfo: PageInfoService,
    private layout: LayoutService
  ) {}

  ngOnInit(): void {
    this.title$ = this.pageInfo.title.asObservable();
    this.description$ = this.pageInfo.description.asObservable();
    this.bc$ = this.pageInfo.breadcrumbs.asObservable();
    this.showTitle = this.layout.getProp('pageTitle.display') as boolean;
    this.showBC = this.layout.getProp('pageTitle.breadCrumbs') as boolean;
    this.pageTitleCssClass = this.layout.getStringCSSClasses('pageTitle');
    this.pageTitleDirection = this.layout.getProp(
      'pageTitle.direction'
    ) as string;
  }

  ngOnDestroy() {
    this.unsubscribe.forEach((sb) => sb.unsubscribe());
  }
}
<ng-container *ngIf="title$ | async as title">
  <ng-container *ngIf="showTitle">
    <!-- begin::Title -->
    <div class="page-title d-flex flex-column me-3">
      <!--begin::Title-->
      <h1 class="d-flex text-white fw-bolder my-1 fs-3">{{ title }}</h1>
      <!--end::Title-->

      <!--begin::Breadcrumb-->
      <ng-container *ngIf="showBC">
        <ng-container *ngIf="bc$ | async as _bc">
          <ng-container *ngIf="_bc.length > 0">
            <ng-container *ngIf="pageTitleDirection === 'row'">
              <span class="h-20px border-gray-200 border-start mx-4"></span>
            </ng-container>
            <ul class="breadcrumb breadcrumb-separatorless fw-bold fs-7 my-1">
              <li
                *ngFor="let bc of _bc"
                class="breadcrumb-item"
                [ngClass]="!bc.isSeparator && bc.isActive ? 'text-dark' : ''"
                [ngClass]="!bc.isSeparator && !bc.isActive ? 'text-muted' : ''"
              >
                <ng-container *ngIf="!bc.isSeparator">
                  <a class="text-muted text-hover-primary" [routerLink]="bc.path">
                    {{ bc.title }}
                  </a>
                </ng-container>
                <ng-container *ngIf="bc.isSeparator">
                  <span class="bullet bg-gray-200 w-5px h-2px"></span>
                </ng-container>
              </li>
              <li class="breadcrumb-item text-dark">{{ title }}</li>
            </ul>
          </ng-container>
        </ng-container>
      </ng-container>
      <!--end::Breadcrumb-->
    </div>
    <!-- end::Title -->
  </ng-container>
</ng-container>
